if(typeof(EventSource)!=="undefined"){var source=new EventSource("play-status");source.onmessage=function(b){var d=JSON.parse(b.data);var a=document.getElementById("myvideo");if(a.src!==d.url){a.src=d.url}var c=Math.abs(a.currentTime-d.seconds);if(c>1){a.currentTime=d.seconds}if(d.playing&&a.paused){a.currentTime=d.seconds;a.play()}else{if(!d.playing&&!a.paused){a.currentTime=d.seconds;a.pause()}}}}else{console.log("No event source")}function toggle(a){a.paused?a.play():a.pause()}window.onload=function(){var b=document.getElementById("myvideo");var a=document.getElementById("url");a.onkeydown=function(c){if(c.which==13){$.post("play-url",{url:a.value});this.value=""}};document.onkeypress=function(c){if(c.which==32&&document.activeElement!==a){toggle(b)}};b.addEventListener("play",function(){$.get("play")});b.addEventListener("pause",function(){$.get("pause")});b.addEventListener("seeked",function(){$.get("seek",{currentTime:this.currentTime})})};